function applyBloggerFilter(el) { var label = el.value; if (label === "") { window.location.href = "/"; } else { window.location.href = "/search/label/" + encodeURIComponent(label); } } function applyBloggerSort(el) { var sort = el.value; if (sort === "alpha") { window.location.href = window.location.pathname + "?orderby=title"; } else { window.location.href = window.location.pathname; } }
